// MARK: - Sample Telemetry Points
let sampleSpeedPoints: [TelemetryPoint] = {
    let n = min(sampleTimes.count, sampleSpeeds.count)
    return (0..<n).map { TelemetryPoint(time: sampleTimes[$0], value: sampleSpeeds[$0]) }
}()

let sampleThrottlePoints: [TelemetryPoint] = {
    let n = min(sampleTimes.count, sampleThrottles.count)
    return (0..<n).map { TelemetryPoint(time: sampleTimes[$0], value: sampleThrottles[$0]) }
}()

let sampleBrakePoints: [TelemetryPoint] = {
    let n = min(sampleTimes.count, sampleBrakes.count)
    return (0..<n).map {
        let value: Double = sampleBrakes[$0] == true ? 1.0 : 0.0
        return TelemetryPoint(time: sampleTimes[$0], value: value)
    }
}()

let samplePositionPoints: [TelemetryPoint<CGPoint>] = {
    return (0..<sampleTimes.count).map {
        return TelemetryPoint<CGPoint>(time: sampleTimes[$0], value: sampleCarPoints[$0])
    }
}()

let sampleDirections: [TelemetryPoint<Double>] = {
    let points: [CGPoint] = samplePositionPoints.map { $0.value }
    let directions = points.directions()
    return (0..<sampleTimes.count).map {
        return TelemetryPoint<Double>(time: sampleTimes[$0], value: directions[$0])
    }
}()
